import React from "react";
import data from "./db.json";

const Header = (props) => {
    const { name, hasName, children } = props;
    // const {name} = props;
    return (
        <div>

        <h1
        className = "header--name"
        style={{
            backgroundColor: "gray",
        }}
        >

            Hello {hasName && name}
            </h1>
            <h2>Menus</h2>
            <div>{children}</div>
            </div> 
    );
};



const App = () => {
    const hasHeader = true;
    const name = "Cho Hasang";


    return (
        <div>
            <header>
                {hasHeader ? <Header name={name} hasName >asd</Header> : null}
            </header>
            <main>
                <div>
                    <h3>{data.todos[0].title}</h3>
                    <span>{data.todos[0].createdAt}</span>
                    <span>{data.todos[0].completed ? "✅" : "❎"}</span>
                </div>
                <div>
                    <h3>{data.todos[1].title}</h3>
                    <span>{data.todos[1].createdAt}</span>
                    <span>{data.todos[1].completed ? "✅" : "❎"}</span>
                </div>
            </main>
        </div>
    );
};

//export { App };
export default App;
